"use strict";var cleanRoom=function(){return new Promise(function(e,n){setTimeout(function(){e("room is cleaned")},1e3)})},throwGarbage=function(){return new Promise(function(e,n){setTimeout(function(){e("Garbage is thrown")},5e3)})},winIceCream=function(){return new Promise(function(e,n){setTimeout(function(){e("Bought Ice Cream")},1e4)})};Promise.all([cleanRoom(),throwGarbage(),winIceCream()]).then(function(){console.log("all of them is finished")}).catch(function(e){console.log(e)});